  function handleChange(e) {
    const { name, value } = e.target
    setInputs((prevInputs) => ({
      ...prevInputs,
      [name]: value,
    }))
  }
  return (
    <div>
      <h1 className='home-header'>Drug Interaction Checker</h1>
      <SearchMeds />
    </div>
  )
}

export default Home
