import React from 'react'
import styled from 'styled-components'

const MedNameContainer = styled.div`
    width: 100%;
    height: 2em;
    display: flex;
    border-bottom: 1px solid #d8d8d878;
    padding: 6px 8px;
`
const Thumbnail = styled.div`
    width: auto;
    height: 100%;
    display: flex;
    flex: 1;

    img {
        width: auto;
        height: 100%;
    }
`

const Name = styled.h3`
    font-size: 20px;
    color: #000;
    margin-left: 10px;
    display: flex;
    flex: 2;
`
const Rxcui = styled.span`
    color: #a1a1a1;
    font-size: 16px;
    display: flex;
    flex: 1;
`

export function MedNameLookup(props) {
    const { thumbnailSrc, name, rxcui } = props
    return (
        <MedNameContainer>
            <Thumbnail src={thumbnailSrc}/>
            <Name> { name } </Name>
            <Rxcui>{rxcui}</Rxcui>
        </MedNameContainer>
    )
}
