        let newArr = ""
        for (i = 0; i < med.length-1; i++) {
            newArr += med[i] + "+"

        }
    })
    console.log(rxcuiCollection)

    console.log("selectedMeds map: ", selectedMeds.map(drug => drug.rxcui+"+"))
    console.log(rxcuiCollection)
