import axios from 'axios'
import React, { useState, useEffect, useContext } from 'react'
import { UserContext } from '../context/UserProvider.js'


function NotesForm(props) {

    const { addNote } = props
    const initInputs = {
        notes: ""
    }

    const [inputs, setInputs] = useState(initInputs)
    const [allNotes, setAllNotes] = useState([])
    const { getUserNotes } = useContext(UserContext)

    function addNotes() {
        axios.post("/api/notes", {notes})
            .then(res => {
                // const { notes } = res.data
                console.log(res.data)
                setInputs(prevInputs => ({
                    ...prevInputs
                }))
                console.log("notes: ", inputs)
            })
            .catch(err => console.log(err))
    }


    function handleChange(e) {
        const { name, value } = e.target
        setInputs(prevInputs => ({ ...prevInputs, [name]: value }))
    }

    function handleSubmit(e) {
        e.preventDefault()
        addNote(inputs)
        setInputs(initInputs)
    }

    function getNotes() {
        axios.get("/notes")
            .then(res => {
                setAllNotes(res.data)
            })
            .catch(err => console.log(err.response.data.errMsg))
    }

    function handleDelete(noteId) {
        axios.delete(`/notes/${noteId}`)
            .then(res => {
                setAllNotes(prevState => prevState.filter(note => note._id !== noteId))
            })
            .catch(err => console.log(err.response.data.errMsg))
    }

    const { notes } = inputs

    //this works but is this the best practice, as it triggers useeffect with each keystroke?
    useEffect(() => {
        getNotes()
        getUserNotes()

        console.log("useeffect ran")
    }, [notes])

    return (
        <>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="notes"
                    value={notes}
                    onChange={handleChange}
                    placeholder="Notes"
                />
                <button> Submit </button>
            </form>
            <div>
                {allNotes.map(note => <p key={note._id}>{note.notes} <button onClick={() => handleDelete(note._id)}>delete</button></p>)}
            </div>
        </>
    )
}

export default NotesForm
