import axios from 'axios'
import React, { useState, useEffect } from 'react'


function NotesForm(props) {

    const initInputs = {
        notes: ""
    }

    const [inputs, setInputs] = useState(initInputs)
    const [allNotes, setAllNotes] = useState([])

    function addNotes() {
        axios.post("/notes", {notes})
            .then(res => {
                console.log(res.data)
                setInputs(prevInputs => ({
                    ...prevInputs
                }))
                console.log("notes: ", inputs)
            })
            .catch(err => console.log(err.response.data.errMsg))
    }


    function handleChange(e) {
        const { name, value } = e.target
        setInputs(prevInputs => ({ ...prevInputs, [name]: value }))
    }

    function handleSubmit(e) {
        e.preventDefault()
        addNotes()
        setInputs(initInputs)
    }

    function getNotes() {
        axios.get("/notes")
            .then(res => {
                setAllNotes(res.data)
            })
            .catch(err => console.log(err.response.data.errMsg))
    }

    function handleDelete(noteId) {
        axios.delete(`/notes/${noteId}`)
            .then(res => {
                setAllNotes(prevState => prevState.filter(note => note._id !== noteId))
            })
            .catch(err => console.log(err.response.data.errMsg))
    }

    const deleteMedList = (medId) => {
        axios.delete(`/rxlist/${medId}`)
            .then(res => {
                console.log(medId)
                console.log(selectedMeds.filter(med => med._id !== medId))
                setSelectedMeds(prevState => prevState.filter(med => med._id !== medId))
            })
            .catch(err => console.log(err.response.data.errMsg))
    }
    const { notes } = inputs

    useEffect(() => {
        getNotes()
    }, [allNotes])

    return (
        <>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="notes"
                    value={notes}
                    onChange={handleChange}
                    placeholder="Notes"
                />
                <button> Submit </button>
            </form>
            <div>
                {allNotes.map(note => <p key={note._id}>{note.notes} <button onClick={handleDelete}>delete</button></p>)}
            </div>
        </>
    )
}

export default NotesForm
